import React, { useEffect, useState } from "react";
import axios from "axios";
import Room from "../components/Room";
import Loading from "../components/Loading.tsx";
import moment from "moment";
import { DatePicker } from "antd";
const { RangePicker } = DatePicker;

function DashBoard() {
  const [room, setRoom] = useState([]);
  const [loading, setLoading] = useState();
  const [error, setError] = useState();
  const [fromDate, setFromDate] = useState();
  const [toDate, setToDate] = useState();
  const [duplicateRoom, setDuplicateRoom] = useState([]);

  const checkDate = (date) => {
    let tempRooms = [];
    let availability = false;

    //if fromdate or todate equal input then not show unavailable room
    for (const room of duplicateRoom) {
      if (room.currentbookings.length > 0) {
        for (const booking of room.currentbookings) {
          if (
            //check input date is between in db
            !moment(moment(date[0].$d).format("DD-MM-YYYY")).isBetween(
              booking.fromdate,
              booking.todate
            ) &&
            !moment(moment(date[1].$d).format("DD-MM-YYYY")).isBetween(
              booking.fromdate,
              booking.todate
            )
          ) {
            // if (
            //   //if fromdate/todate input different than value from/todate in db
            //   moment(date[0].$d).format("DD-MM-YYYY") !== booking.fromdate &&
            //   moment(date[0].$d).format("DD-MM-YYYY") !== booking.todate &&
            //   moment(date[1].$d).format("DD-MM-YYYY") !== booking.fromdate &&
            //   moment(date[1].$d).format("DD-MM-YYYY") !== booking.todate
            // )
            //then can room available
            availability = true;
          }
        }
      }
      if (availability === true || room.currentbookings.length === 0) {
        tempRooms.push(room);
      }
      setRoom(tempRooms);
    }
  };
  const filterByDate = (date) => {
    checkDate(date);
    setFromDate(moment(date[0].$d).format("DD-MM-YYYY"));
    setToDate(moment(date[1].$d).format("DD-MM-YYYY"));
  };

  useEffect(() => {
    async function fetchData() {
      try {
        setLoading(true);
        const res = await axios.get("//localhost:5000/api/room/getallrooms");
        setRoom(res.data);
        setDuplicateRoom(res.data);
        console.log(res.data);
        setLoading(false);
      } catch (error) {
        setError(true);
        console.log(error);
        setLoading(false);
      }
    }
    fetchData();
  }, []);

  return (
    <div className="container">
      <div className="row mt-5">
        <div className="col-md-3">
          <RangePicker format={"DD-MM-YYYY"} onChange={filterByDate} on />
        </div>
      </div>
      <div className="row justify-content-center mt-5">
        {loading ? (
          <h1>
            <Loading />
          </h1>
        ) : (
          room.map((room) => {
            return (
              <div className="col-md-9 mt-2">
                <Room room={room} fromDate={fromDate} toDate={toDate} />
              </div>
            );
          })
        )}
      </div>
    </div>
  );
}

export default DashBoard;
