  //         if (
  //           //check input date is between in db
  //           !moment(moment(date[0].$d).format("DD-MM-YYYY")).isBetween(
  //             booking.fromdate,
  //             booking.todate
  //           ) &&
  //           !moment(moment(date[1].$d).format("DD-MM-YYYY")).isBetween(
  //             booking.fromdate,
  //             booking.todate
  //           )
  //         )
  //           if (
  //             //if fromdate/todate input different than value from/todate in db
  //             moment(date[0].$d).format("DD-MM-YYYY") !== booking.fromdate &&
  //             moment(date[0].$d).format("DD-MM-YYYY") !== booking.todate &&
  //             moment(date[1].$d).format("DD-MM-YYYY") !== booking.fromdate &&
  //             moment(date[1].$d).format("DD-MM-YYYY") !== booking.todate
  //           )
  //             //then can room available
  //             availability = true;
  //       }
  //     }
  //     if (availability === true || room.currentbookings.length === 0) {
  //       tempRooms.push(room);
  //     }
  //     setRoom(tempRooms);
  //   }
  // };
  const filterByDate = (date) => {
    setFromDate(moment(date[0].$d).format("DD-MM-YYYY"));
    setToDate(moment(date[1].$d).format("DD-MM-YYYY"));
    var tempRooms = [];
    var availability = false;

    for (const room of duplicateRoom) {
      if (room.currentbookings.lenght > 0) {
        for (const booking of room.currentbookings) {
          if (
            !moment(moment(date[0].$d).format("DD-MM-YYYY")).isBetween(
              booking.fromdate,
              booking.todate
            ) &&
            !moment(moment(date[1].$d).format("DD-MM-YYYY")).isBetween(
              booking.fromdate,
              booking.todate
            )
          ) {
            if (
              fromDate !== booking.fromdate &&
              fromDate !== booking.todate &&
              toDate !== booking.fromdate &&
              toDate !== booking.todate
            ) {
              availability = true;
            }
          }
        }
      }
      if (availability === true || room.currentbookings.lenght > 0) {
        tempRooms.push(room);
      }
      setRoom(tempRooms);
    }
    //if fromdate or todate equal input then not show unavailable room
    // for (const room of duplicateRoom) {
    //   if (room.currentbookings.length > 0) {
    //     for (const booking of room.currentbookings) {
    //       if (
    //         //check input date is between in db
    //         !moment(moment(date[0].$d).format("DD-MM0")).isBetween(booking.fromdate, booking.todate) &&
    //         !moment(toDate).isBetween(booking.fromdate, booking.todate)
    //       )
    //         if (
    //           //if fromdate/todate input different than value from/todate in db
    //           fromDate !== booking.fromdate &&
    //           fromDate !== booking.todate &&
    //           toDate !== booking.fromdate &&
    //           toDate !== booking.todate
    //         )
    //           //then can room available
    //           availability = true;
    //     }
    //   }
    //   if (availability === true || room.currentbookings.length === 0) {
    //     tempRooms.push(room);
    //   }
    //   setRoom(tempRooms);
    // }
  };

  useEffect(() => {
    async function fetchData() {
      try {
        setLoading(true);
        const res = await axios.get("//localhost:5000/api/room/getallrooms");
        setRoom(res.data);
        setDuplicateRoom(res.data);
        console.log(res.data);
        setLoading(false);
      } catch (error) {
        // setError(true);
        console.log(error);
        setLoading(false);
      }
    }
    fetchData();
  }, []);

  return (
    <div className="container">
      <div className="row mt-5">
        <div className="col-md-3">
          <RangePicker format={"DD-MM-YYYY"} onChange={filterByDate} />
        </div>
      </div>
      <div className="row justify-content-center mt-5">
        {loading ? (
          <h1>
            <Loading />
          </h1>
        ) : (
          room.map((room) => {
            return (
              <div className="col-md-9 mt-2">
                <Room room={room} fromDate={fromDate} toDate={toDate} />
              </div>
            );
          })
        )}
      </div>
    </div>
  );
}

export default DashBoard;
