import React, { useEffect, useState } from "react";
import axios from "axios";
import Room from "../components/Room";
import Loading from "../components/Loading.tsx";
// import { DateRangePicker } from "rsuite";

// import DatePicker from "react-datepicker";
// import "react-datepicker/dist/react-datepicker.css";

import moment from "moment";
import { DatePicker } from "antd";
const { RangePicker } = DatePicker;

function DashBoard() {
  const [room, setRoom] = useState([]);
  const [loading, setLoading] = useState();
  const [error, setError] = useState();
  const [fromDate, setFromDate] = useState();
  const [toDate, setToDate] = useState();
  // const styles = { width: 260, display: "block", marginBottom: 10 };
  useEffect(() => {
    async function fetchData() {
      try {
        setLoading(true);
        const res = await axios.get("//localhost:5000/api/room/getallrooms");
        console.log(res.data);
        setRoom(res.data);
        setLoading(false);
      } catch (error) {
        setError(true);
        console.log(error);
        setLoading(false);
      }
    }
    fetchData();
  }, []);

  // function filterByDate(dates) {
  //   setFromDate(moment(dates[0]).format("DD-MM-YYYY"));
  //   setToDate(moment(dates[1]).format("DD-MM-YYYY"));
  //   console.log(dates);

  //   console.log(moment(dates[0]).format("DD-MM-YYYY"));
  //   console.log(moment(dates[1]).format("DD-MM-YYYY"));
  // }

  return (
    <div className="container">
      <div className="row mt-5">
        <div className="col-md-3">
          <RangePicker
            onChange={(date) => {
              console.log(moment(date[0]._i).format("DD-MM-YYYY"));
              setFromDate(moment(date[0]).format("YYYY-MM-DD"));
              setToDate(moment(date[1]).format("YYYY-MM-DD"));
            }}
          />
        </div>
      </div>
      <div className="row justify-content-center mt-5">
        {loading ? (
          <h1>
            <Loading />
          </h1>
        ) : error ? (
          <h1>
            <Loading />
          </h1>
        ) : (
          room.map((room) => {
            return (
              <div key={"roomRender"} className="col-md-9 mt-2">
                <Room room={room} fromDate={fromDate} toDate={toDate} />
              </div>
            );
          })
        )}
      </div>
    </div>
  );
}

export default DashBoard;
