import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Loading from "../components/Loading.tsx";
import moment from "moment";

function BookingScreen() {
  //lay room id tu du lieu hien tai dung useParams
  const { roomid, fromDate, toDate } = useParams();

  const [room, setRoom] = useState();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState();
  const toMoment = moment(toDate, "DD-MM-YYYY");
  const fromMoment = moment(fromDate, "DD-MM-YYYY");
  const totalDay =
    moment.duration(toMoment.diff(fromMoment)) * (24 * 60 * 60 * 1000);

  useEffect(() => {
    async function fetchRoom() {
      try {
        setLoading(true);
        const res = (
          await axios.post("//localhost:5000/api/room/getroombyID", {
            roomid: roomid,
          })
        ).data;
        // console.log(res);
        console.log(totalDay);
        setRoom(res);
        setLoading(false);
      } catch (error) {
        setLoading(false);
        setError(true);
        console.log(error);
      }
    }
    fetchRoom();
  }, [roomid]);
  return (
    <div className="m-5">
      {loading ? (
        <h1>
          <Loading />
        </h1>
      ) : error ? (
        <h1>
          <Loading />
        </h1>
      ) : (
        <div className="row justify-content-center mt-5 room">
          <div className="col-md-6">
            <h1>{room.name}</h1>
            <img src={room.imageurls[0]} alt="html5" className="bigimg" />
          </div>
          <div className="col-md-6">
            <div className="bookingdetails">
              <h1>Chi tiết đặt phòng</h1>
              <hr />
              <b>
                <p>Name:</p>
                <p>From: {fromDate}</p>
                <p>To: {toDate}</p>
                <p>MaxCount: {room.maxcount}</p>
              </b>
            </div>

            <div className=" bookingdetails">
              <h1>Amount:</h1>
              <b>
                <hr />
                <p>Total days:</p>
                <p>Rentperday: {room.rentperday}</p>
                <p>Total Amount:</p>
              </b>
            </div>

            <div style={{ float: "right" }}>
              <button className="btn">Book Now</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default BookingScreen;
